import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import "../index.css";
import { Link } from "react-router-dom";

export const LoginForm = () => {
  // schema for object being created from the text boxes
  const schema = yup.object().shape({
    username: yup.string().required("Your username is required!"),
    password: yup
      .string()
      .min(6)
      .max(16)
      .required("Your password is required!"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors }, // get the messages for the errors
  } = useForm({
    resolver: yupResolver(schema), // resolver from yup to pass the schema in
  });

  /*
    Need to handle this onSubmit we need to take data to backend and
    forward the user to the landing page
  */
  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <div className="login-body">
      <div className="login-container">
        <div>
          <h2>Login To Your Account</h2>
        </div>
        <div>
          <form onSubmit={handleSubmit(onSubmit)}>
            <input
              type="text"
              placeholder="Username..."
              {...register("username")}
            ></input>
            <p className="error-msg">{errors.username?.message}</p>
            <input
              type="text"
              placeholder="Password..."
              {...register("password")}
            ></input>
            <p className="error-msg">{errors.password?.message}</p>
            <p>
              No Account? <Link to="/create-account">Register Today!</Link>
            </p>
            <input className="btn" type="submit"></input>
          </form>
        </div>
      </div>
    </div>
  );
};
